<?php
/**
 * @var string $url
 * @var string $status
 * @var array  $message
 */

if (!empty($message) && !is_array($message)) {
	$message = (array)$message;
}
if (!empty($message) && is_array($message)) {
	foreach ($message as &$val) {
		$val = e($val);
	}
	$message[] = 'Или нажмите на <a href="'. url($url) .'" target="_parent">эту ссылку</a>';
}

$img = null;

if ($status == 'danger') {
	$img = 'data:image/png;base64,';
}

if ($status == 'success') {
	$img = 'data:image/png;base64,';
}

?>
<html lang="ru">
<head>
	<title>Return to Shop</title>
	<meta charset="UTF-8">
</head>
<body>

<div style="border: 1px solid grey; padding: 25px; margin: 10px; font-family: Trebuchet MS, sans-serif;">
	<?php if ($img) { ?>
		<img style="border: 0; margin: 15px; float: left;" src="<?= $img ?>">
	<?php } ?>
	<pre style="font-family: Trebuchet MS, sans-serif;"><?= implode('<br>', $message) ?></pre>
