<script type="application/javascript">

	function reloadParentOrThis() {
		try {
			var url = '<?= url($url) ?>';
			if (window.parent && window.parent.location) {
				window.parent.location.replace(url);
			} else {
				window.location.replace(url);
			}
		} catch (e) {
		}
	}
	setTimeout(reloadParentOrThis, 3000);

</script>

